riot.tag2("af-tabbar",'<ul><li each="{tab, i in tabs}" class="tab {is-active: parent.isActiveTab(tab.id)}" onclick="{parent.toggleTab}">{tab.title}</li></ul><div class="after"></div>',"","",function(t){var a=this;a.tabs=t.tabs,a.activeTab=t.active||t.tabs&&t.tabs[0],this.isActiveTab=function(t){return this.activeTab.id===t},this.toggleTab=function(t){var e=a.root.getElementsByClassName("after")[0];e.style.left=t.target.offsetLeft+"px",e.style.width=t.target.offsetWidth+"px",a.activeTab=t.item.tab,a.opts.bus&&a.opts.bus.trigger("tabchanged",t.item.tab)},this.on("mount",function(){a.toggleTab({target:a.root.getElementsByClassName("is-active")[0],item:{tab:a.activeTab}})})});